const Styledsidebar = styled.div`
    background-color: #1b1b1b;
    width: 25%;
    min-height: 100%;
    position: fixed;
    /* border-top-right-radius: 100%;
    border-bottom-right-radius: 100%; */
    letter-spacing: 3px;
    hr {
        background-color: white;
        width: 85%;
        height: 3px;
        border-radius: 50px;
    }
    
    .title {
        color: white;
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        margin: 1rem 0rem 1rem 0rem;
        padding: 2rem 1rem 0rem 1rem;
        svg {
            height: 50%;
            #signature {
                stroke-dasharray: 4219;
                stroke-dashoffset: 4219;
                animation: sign 6s infinite;
                animation-fill-mode: forwards;
                @keyframes sign {
                    to {
                        stroke-dashoffset: 0;
                    }
                }
            }   
        }
       
       
        h1 {
            font-weight: 450;
            margin: 0;
        }
        
        h2 {
            text-align: center;
            font-weight: 100;
            margin: .5rem;
        }
        img {
            width: 25%;
            height: 25%;
            background-color: white;
            border-radius: 75px;
            padding: .15em;

        }
    }
    .list {
        color: white;
        display: flex;
        flex-direction: column;
        p {
            width: 65%;
            margin: .5rem 0rem .5rem 0rem;
            padding: .5rem 0rem .5rem 2rem;
            cursor: pointer;
            transition: all 0.5s ease-in-out;
            border-radius: 50px;
            &:hover {
                background-color: white;
                color: black;
            } 
        }
    }
    .contact-info {
        padding: .25rem;
        margin-top: 1rem;
        /* background-color: white; */
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        .icons {
            /* background-color: violet; */
            width: 100%;
            display: flex;
            flex-direction: row;
            align-items: center;
            justify-content: space-evenly;
            cursor: pointer;
            .icon {
                /* width: 25%; */
                padding: .5rem;
                border-radius: 50px;
            }
        }
    }

`;

